SELECT URL_TX,substr("  "||n.SL_NO,-3),n.SL_NAME,SL_TYPE,ifnull(sl_time,(select t.map_name from map_info t where t.MAP_NO = m.SL_DROP)||ifnull(m.SL_LIMIT,"")) as sl_time,substr(" "||SL_HP,-3) as sl_hp,SL_GJ,substr(" "||SL_FY,-2),SL_MZ,SL_SB,SL_BJ,substr(" "||SL_GS,-2) as sl_gs,SL_SY,SL_ML,MAX_HP,MAX_GJ,MAX_FY,substr(" "||MAX_MZ,-4),substr(" "||MAX_SB,-4),MAX_SY,MAX_ML FROM fairy_detail n,fairy_detail_max m WHERE n.SL_NO=m.SL_NO and n.hb_open = 1