SELECT URL_TX,n.SL_NO,n.SL_NAME,SL_TYPE,ifnull(sl_time,(select t.map_name from map_info t where t.MAP_NO = m.SL_DROP)||ifnull(m.SL_LIMIT,"")) as sl_time,SL_HP,SL_GJ,SL_FY,SL_MZ,SL_SB,SL_BJ,SL_GS,SL_SY,SL_ML,MAX_HP,MAX_GJ,MAX_FY,MAX_MZ,MAX_SB,MAX_SY,MAX_ML FROM fairy_detail n,fairy_detail_max m WHERE n.SL_NO=m.SL_NO and n.hb_open = 1